    }

    // 差值的最大取值
    let maxDifference = 0;

    // 计算差值的最大取值
    for (let i = 0; i < length - 1; i++) {
      let leftSum = prefixSum[i];
      let rightSum = prefixSum[length-1] - prefixSum[i];
