#include <iostream>
#include <sstream>
#include <vector>
#include <algorithm>
using namespace std;

int main() {
  string line;
  getline(cin, line);
  istringstream iss(line);

  vector<int> counts;
  int count;
  while (iss >> count) {
    counts.push_back(count);
  }

  int totalPass = 0;
  for (int i = 0; i < counts.size(); i++) {
    totalPass += counts[i];
  }

  int numberOfPeople = counts.size();
  vector<int> peopleCounts(numberOfPeople, 0);

  int currentNumber = 1;
  int currentIndex = 0;
  while (totalPass > 0) {
    if (currentNumber % 7 == 0 || to_string(currentNumber).find("7") != string::npos) {
      totalPass--;
      peopleCounts[currentIndex]++;
    }
    currentNumber++;
    currentIndex = (currentIndex + 1) % numberOfPeople;
  }

  string output;
  for (int i = 0; i < peopleCounts.size(); i++) {
    output += to_string(peopleCounts[i]) + " ";
  }
  output = output.substr(0, output.size() - 1);

  cout << output << endl;

  return 0;
}

