#include <iostream>
#include <cmath>
using namespace std;

int main() {
    long long num_people, len_letter;
    cin >> num_people >> len_letter;
    long long min_len_num = max(1LL, (long long) ceil(log10(num_people / pow(26, len_letter))));
    cout << min_len_num << endl;
    return 0;
}

