#include <stdio.h>
#include <math.h>

int main() {
    long long num_people, len_letter;

    scanf("%lld %lld", &num_people, &len_letter);

    long long min_len_num = (long long) fmax(1.0, ceil(log10(num_people / pow(26, len_letter))));

    printf("%lld\n", min_len_num);

    return 0;
}
