#include <iostream>
#include <string>
using namespace std;

int main() {
    // 创建一个字符串变量，用于存储用户输入
    string input;
    // 读取用户输入的字符串
    getline(cin, input);
    // 获取字符串的长度
    int len = input.size();
    // 初始化'o'字符的计数器
    int num = 0;
    // 遍历字符串，统计'o'字符的数量
    for (char chr : input) {
        if (chr == 'o') {
            num += 1;
        }
    }
    // 如果'o'字符出现的次数是偶数，则输出字符串的长度
    if (num % 2 == 0) {
        cout << len << endl;
    } else {
        // 如果'o'字符出现的次数是奇数，则输出字符串长度减1
        cout << len - 1 << endl;
    }
    return 0;
}

codes.c:
#include <stdio.h>
#include <string.h>

int main() {
    char input[500001]; // 创建一个字符数组，用于存储用户输入的字符串，最大长度500000
    scanf("%s", input); // 读取用户输入的字符串

    int len = strlen(input); // 获取字符串的长度
    int num = 0; // 初始化'o'字符的计数器

    // 遍历字符串，统计'o'字符的数量
    for (int i = 0; i < len; i++) {
        if (input[i] == 'o') {
            num++;
        }
    }

    // 如果'o'字符出现的次数是偶数，则输出字符串的长度
    if (num % 2 == 0) {
        printf("%d\n", len);
    } else {
        // 如果'o'字符出现的次数是奇数，则输出字符串长度减1
        printf("%d\n", len - 1);
    }

    return 0;
}
