#include <iostream>
#include <cmath>

// 函数：检查一个数是否为素数
bool isPrime(int num) {
    if (num <= 3) {
        return num > 1;
    }
    if (num % 6 != 1 && num % 6 != 5) {
        return false;
    }
    for (int i = 5; i <= sqrt(num); i += 6) {
        if (num % i == 0 || num % (i + 2) == 0) {
            return false;
        }
    }
    return true;
}

int main() {
    int num;
    std::cin >> num; // 读取输入

    // 如果数字本身就是素数，那么它不能被分解
    if (isPrime(num)) {
        std::cout << "-1 -1" << std::endl;
        return 0;
    }

    // 分解数字
    for (int i = 2; i <= sqrt(num); i++) {
        if (num % i == 0) {
            int j = num / i;
            // 检查 i 和 j 是否都是素数
            if (isPrime(i) && isPrime(j)) {
                std::cout << (i < j ? std::to_string(i) + " " + std::to_string(j) : std::to_string(j) + " " + std::to_string(i)) << std::endl;
                return 0;
            }
        }
    }
    std::cout << "-1 -1" << std::endl;
    return 0;
}

