#include <stdio.h>
#include <math.h>

// 函数：检查一个数是否为素数
int isPrime(int num) {
    if (num <= 3) {
        return num > 1;
    }
    if (num % 6 != 1 && num % 6 != 5) {
        return 0;
    }
    for (int i = 5; i <= sqrt(num); i += 6) {
        if (num % i == 0 || num % (i + 2) == 0) {
            return 0;
        }
    }
    return 1;
}

int main() {
    int num;
    scanf("%d", &num); // 读取输入

    // 如果数字本身就是素数，那么它不能被分解
    if (isPrime(num)) {
        printf("-1 -1\n");
        return 0;
    }

    // 分解数字
    for (int i = 2; i <= sqrt(num); i++) {
        if (num % i == 0) {
            int j = num / i;
            // 检查 i 和 j 是否都是素数
            if (isPrime(i) && isPrime(j)) {
                printf("%d %d\n", i < j ? i : j, i < j ? j : i);
                return 0;
            }
        }
    }
    printf("-1 -1\n");
    return 0;
}
