#include <iostream>
#include <cmath>
using namespace std;

int main() {
    long long x;
    int y;
    cin >> x >> y;

    double result = log10((double)x / pow(26, y));
    int z = (int)ceil(result);
    if (z < 1) z = 1;

    cout << z << endl;
    return 0;
}

codes.c:
#include <stdio.h>
#include <math.h>

int main() {
    long long x;
    int y;
    scanf("%lld %d", &x, &y);

    double result = log10((double)x / pow(26, y));
    int z = (int)ceil(result);
    if (z < 1) z = 1;

    printf("%d\n", z);
    return 0;
}
