codes.cpp:
#include <iostream>
#include <string>
using namespace std;

int main() {
    string s;
    cin >> s;

    int n = s.length();
    int ans = 0;

    for (int i = 0; i < n; i++) {
        if (s[i] == 'M') {
            if (i + 1 < n && s[i + 1] == 'I') {
                ans++;
                i += 2;
            } else if (i - 1 >= 0 && s[i - 1] == 'I') {
                ans++;
            } else {
                ans = -1;
                break;
            }
        }
    }

    cout << ans << endl;
    return 0;
}

