#include <stdio.h>
#include <string.h>

int main() {
    char s[10001];
    scanf("%s", s);

    int n = strlen(s);
    int ans = 0;

    for (int i = 0; i < n; i++) {
        if (s[i] == 'M') {
            if (i + 1 < n && s[i + 1] == 'I') {
                ans++;
                i += 2;
            } else if (i - 1 >= 0 && s[i - 1] == 'I') {
                ans++;
            } else {
                ans = -1;
                break;
            }
        }
    }

    printf("%d\n", ans);
    return 0;
}
