#include <iostream>
#include <string>
using namespace std;

int main() {
    string line;
    // 读取输入的表面读数
    getline(cin, line);
    // 初始化实际产生的费用
    int correct = 0;
    // 遍历读数的每一位数字
    for (char c : line) {
        int digit = c - '0'; // 将字符转换为数字
        if (digit > 4) { // 如果数字大于4，则需要减1
            digit--;
        }
        correct = correct * 9 + digit; // 将每一位数字加入到实际产生的费用中
    }
    // 输出实际产生的费用
    cout << correct << endl;

    return 0;
}

