#include <stdio.h>
#include <string.h>

int main() {
    char line[10];  
    scanf("%s", line); // 读取输入的表面读数

    int correct = 0; // 初始化实际产生的费用
    int length = strlen(line); // 获取输入字符串的长度

    // 遍历读数的每一位数字
    for (int i = 0; i < length; i++) {
        int digit = line[i] - '0'; // 将字符转换为数字

        // 如果数字大于4，则需要减1，因为跳过了数字4
        if (digit > 4) {
            digit--;
        }

        correct = correct * 9 + digit; // 更新实际产生的费用
    }

    // 输出实际产生的费用
    printf("%d\n", correct);

    return 0;
}
